import React from 'react';
import { StyleSheet, Dimensions,Text, View } from 'react-native';
import Animated, 
{ useValue,useAnimatedScrollHandler, interpolateColor } from 'react-native-reanimated';
import Slide,{SLIDE_HEIGHT} from './Slide';




const {width} = Dimensions.get("screen");
const slides=[
    {label:"Our Team" , color: "#007ffe"},
    {label:"Is Always" , color: "#007ffe"},
    {label:"Ready" , color: "#007ffe"},
    {label:"To Clean" , color: "#007ffe"}
]

const Onboarding = () => {
    const x= useValue(0);
    //const scrollHandler =useAnimatedScrollHandler ;
    const onScroll=onScrollEvent ({x});
    const backgroundColor= interpolateColor (x, {
        inputRange: slides.map((i)=> i* width),
        outputRange: slides.map(slide=>i * slide.color),
    });


    return (
        <View style={styles.container}>
            <View style={styles.slide}>
                <Animated.ScrollView horizontal 
                snapToInterval={width} 
                decelerationRate="fast" 
                showsHorizontalScrollIndicator={false}
                 bounces={false}
                 scrollEventThrottle={1}
                 {...{onScroll}}            


                 >
                     {slides.map(({label}, index) => (
                         <Slide key={index} right={!(index%2)} {...{label}}/>
                     ))}
                    <Slide label="Our Team" />
                    <Slide label="Is Always" right/>
                    <Slide label="Ready"/>
                    <Slide label="To Clean" right/>
                </Animated.ScrollView>
            </View>
            <View style={styles.footer}/>
            <View>

                <View style={{...StyleSheet.absoluteFillObject, backgroundColor: "cyan"}} />
                <View style={{flex:1, backgroundColor:"white", borderTopRightRadius:75,}}>
                </View >
            </View>
        </View>
    )
};

export default Onboarding;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor:"white",
    },
    slide: {
        height: SLIDE_HEIGHT,
        backgroundColor:"#007ffe",
        borderBottomRightRadius:75,

    },
    footer: {
        flex:1,
    },

});
